from browser import document, timer

pi = 0

def next_page(ev):
    global pi
    pi += 1
    document['page0'].style.display = 'none'
    document['page1'].style.display = 'none'
    document['page2'].style.display = 'none'
    document['page3'].style.display = 'none'
    document['page4'].style.display = 'none'
    if pi == 1:
        document['page1'].style.display = 'block'
    elif pi==2:
        document['page2'].style.display = 'block'
    elif pi==3:
        document['page3'].style.display = 'block'
    elif pi==4:
        document['page4'].style.display = 'block'
    else:
        pi = 0
        document['page0'].style.display = 'block'

def wait():
    document['Bg_btn'].bind('click', next_page)

timer.set_timeout(wait, 2000) 